function test_strfind_logical
%
% Unit tests for strfind_logical function
%
    testpattern = 'hello';
    testarray = { 'hello';...
                  'goodbye';...
                  {'hello'};...
                  {'goodbye'};...
                  {'hello' 'goodbye'};...
                  {'hello' 'goodbye'; 'goodbye' 'hello'}...
                };
    answers = {true; false; true; false; [true false]; [true false; false true]};

    for i = 1:length(testarray)
        assert( isequal( strfind_logical(testarray{i}, testpattern), answers{i}) );
    end
end
